svg.script:
const spider = document.getElementById('spider');

// Definir pontos da teia (x, y)
const points = [
  [0, 50], [466, 50], [233, 0], [233, 100],
  [0, 0], [466, 100], [0, 100], [466, 0],
  [60, 50], [406, 50], [233, 50], [150, 25],
  [316, 75], [120, 75], [346, 25], [200, 50]
];

let current = 10; // Começa no centro
let [cx, cy] = points[current];
let [tx, ty] = [cx, cy];

function chooseNextPoint() {
  let next;
  do {
    next = Math.floor(Math.random() * points.length);
  } while (next === current);

  [tx, ty] = points[next];
  current = next;
}

function animate() {
  cx += (tx - cx) * 0.05;
  cy += (ty - cy) * 0.05;

  spider.setAttribute('transform', 'translate(' + cx + ',' + cy + ')');

  // Se estiver suficientemente perto do destino, escolher outro
  if (Math.abs(tx - cx) < 1 && Math.abs(ty - cy) < 1) {
    chooseNextPoint();
  }

  requestAnimationFrame(animate);
}

chooseNextPoint();
animate();
