svg.script:
const spider = document.getElementById('spider');

// Definir pontos da teia (pontos de interseção das linhas)
const points = [
  [250, 50], [400, 100], [450, 250], [400, 400],
  [250, 450], [100, 400], [50, 250], [100, 100],
  [250, 250]
];

let current = 8; // Começa no centro

function moveSpider() {
  let next;
  do {
    next = Math.floor(Math.random() * points.length);
  } while (next === current);

  const [x, y] = points[next];
  spider.setAttribute('transform', 'translate(' + x + ',' + y + ')');
  current = next;
}

setInterval(moveSpider, 2000); // Move a cada 2 segundos
